function getLengthOfWords(words) {    const outArr = [];
    for (const temp of words) {
        outArr.push(temp.length);
    }

    return outArr;
}

function addFiveToNumbers(numbers) {    const outArr = [];
    for (let i = 0; i < numbers.length; i++) {
        outArr.push(numbers[i] + 5)
    }
    return outArr;
}

function sumOfNumbers(numbers) {    let sum = 0;
    for (let i = 0; i < numbers.length; i++) {
        sum += numbers[i];
    }
    return sum;
}

function findLargestNumber(numbers) {    let largest = numbers[0];
    for (let i = 1; i < numbers.length; i++) {
        if (numbers[i] > largest) {
            largest = numbers[i];
        }
    }
    return largest;
}

function countCharacters(string) {    let charCount = {};
    for (let i = 0; i < string.length; i++) {
        let char = string[i];
        if (charCount[char]) {
            charCount[char]++;
        } else {
            charCount[char] = 1;
        }
    }
    return charCount;
}

function changeAllToUppercase(words) {    const outArr = []
    words.forEach(item => {
        outArr.push(item.toUpperCase())
    });

    return outArr;
}

function addTenToNumbers(numbers) {    return numbers.map(item => {
        return item + 10;
    })
}

function sumOfTripledEvens(numbers) {    const evens = numbers.filter((num) => num % 2 === 0);
    const trippled = evens.map((num) => num * 3);
    const sum = trippled.reduce((acc, curVal) => acc + curVal);
    return sum;
}
