function getLengthOfWords(words) {    const outArr = [];
    for (let i = 0; i < words.length; i++) {
        if (words[i].length === 6) {
            continue;
        }
        if (words[i] === "dang") {
            break;
        }
        outArr.push(words[i].length);
    }

    outArr.push(99);
    return outArr;
}

function addFiveToNumbers(numbers) {    const outArr = [];
    for (let i = 0; i < numbers.length; i++) {
        outArr.push(numbers[i] + 5)
    }
    return outArr;
}

function sumOfNumbers(numbers) {    let sum = 0;
    for (let i = 0; i < numbers.length; i++) {
        sum += numbers[i];
    }
    return sum;
}

function findLargestNumber(numbers) {    let largest = numbers[0];
    for (let i = 1; i < numbers.length; i++) {
        if (numbers[i] > largest) {
            largest = numbers[i];
        }
    }
    return largest;
}

function countCharacters(string) {    let charCount = {};
    for (let i = 0; i < string.length; i++) {
        let char = string[i];
        if (charCount[char]) {
            charCount[char]++;
        } else {
            charCount[char] = 1;
        }
    }
    return charCount;
}

function changeAllToUppercase(words) {    const outArr = []
    for (let i = 0; i < words.length; i++) {
        outArr.push(words[i].toUpperCase())
    }

    return outArr;
}
