function getLengthOfWords(words) {    const outArr = [];
    for (const word of words) {
        if (word.length === 6) {
            continue;
        }
        if (word === "dang") {
            break;
        }
        outArr.push(word.length);
    }

    outArr.push(99);
    return outArr;
}

function addFiveToNumbers(numbers) {    const outArr = [];
    for (const number of numbers) {
        outArr.push(number + 5)
    }
    return outArr;
}

function sumOfNumbers(numbers) {    let sum = 0;
    for (const number of numbers) {
        sum += number;
    }
    return sum;
}

function findLargestNumber(numbers) {    let largest = numbers[0];
    for (const number of numbers) {
        if (number > largest) {
            largest = number;
        }
    }
    return largest;
}

function countCharacters(string) {    let charCount = {};
    for (const char of string) {
        if (charCount[char]) {
            charCount[char]++;
        } else {
            charCount[char] = 1;
        }
    }
    return charCount;
}

function changeAllToUppercase(words) {    const outArr = []
    for (const word of words) {
        outArr.push(word.toUpperCase())
    }

    return outArr;
}
